import {FETCH_MALLS} from "./types";
import { instance } from "./instance";

export const fetchMalls = () => async (dispatch) => {
    try {
      const res = await instance.get(`malls/fetch-malls/`);
      dispatch({
        type: FETCH_MALLS,
        payload: res.data,
      });
    } catch (error) {
      dispatch(handleError(error));
    }
  };
